import React, { Component } from 'react';
import { connect } from 'react-redux';
import { createSelector } from 'reselect';
import { bindActionCreators } from 'redux';
import {
  openPanel
} from '../actions/index.js';
import close from '../img/Close.png';
import map from 'lodash/map';
import Chapter from './Chapter.js';
import Mogi from './Mogi.js';
import Chatroom from './Chatroom.js';

class Panel extends Component {
  constructor(props) {
    super(props);

    this.state = {
      desync_panel_open: false,
    }
  }

  componentDidMount() {
    setTimeout(() => {
      this.setState({
        desync_panel_open: true
      })
    }, 10);
  }

  componentDidUpdate(prevProps) {
    const { panel } = this.props;

    if (prevProps.panel.isOpen && !panel.isOpen) {
      this.setState({
        desync_panel_open: false
      })
    }
  }

  panelStyle() {
    const { window_dimensions } = this.props;
    const { desync_panel_open } = this.state;

    if (!desync_panel_open) {
      return {
        transform: `translateY(-${window_dimensions.height+30}px)`
      }
    }
  }

  renderChapters() {
    const { chapters,selected_concept } = this.props;

    return(
      map(
        chapters[selected_concept],
        (chapter, id) => <Chapter key={id} {...chapter} id={id} />
      )
    );
  }

  renderArticle() {
    const { selected_concept } = this.props;

    if (selected_concept === "mogi") {
      return <Mogi />
    }
    else if (selected_concept === "chatroom") {
      return <Chatroom />
    }
  }

  render() {
    const { concepts, selected_concept } = this.props;
    return (
      <div
        style={this.panelStyle()}
        className="panel">
        <div className="panel-top">
          <div className="panel-header">
            <h1 className="big-title">
              {concepts[selected_concept].title}
            </h1>
            <img
              onClick={() => this.props.openPanel(false, "")}
              src={close}
              alt="Close"
              className="close"
            />
          </div>
          <div className="concept-chapters">
            {this.renderChapters()}
          </div>
        </div>
        {this.renderArticle()}
      </div>
    );
  }
}

function matchDispatchToProps(dispatch) {
  return bindActionCreators({
    openPanel
  }, dispatch)
}

const selector = createSelector(
  state => state['window_dimensions'],
  state => state['panel'],
  state => state['chapters'],
  state => state['selected_concept'],
  state => state['concepts'],
  (
    window_dimensions,
    panel,
    chapters,
    selected_concept,
    concepts
) => {
    return  {
      window_dimensions,
      panel,
      chapters,
      selected_concept,
      concepts
    };
  }
);

export default connect(selector, matchDispatchToProps)(Panel);
