import React, { Component } from "react";
import introVideo from "../videos/Portfolio.mp4";
import "../styles/Presentation.css";
import pause from "../img/Pause.svg";
import play from "../img/Play.svg";

export default class Presentation extends Component {
  constructor(props) {
    super(props);

    this.state = {
      paused: true,
      showPresentation: false
    };
  }

  onPlayClick() {
    const { paused } = this.state;
    this.setState({ paused: !paused });
    paused && this.videoRef.play();
    !paused && this.videoRef.pause();
  }

  componentWillReceiveProps(nextProps) {
    const { paused } = this.state;
    if (paused !== nextProps.paused) {
      this.setState({ paused: nextProps.paused });
    }
  }

  render() {
    const { paused } = this.state;
    const videoControl = paused ? play : pause;
    return (
      <div className="section">
        <div className="max-width">
          <p className="section-sub">
            Images are worth more than a thousand words
          </p>
          <h3 className="section-title">About Me</h3>
          <div
            className={
              "video-container " + (paused ? "video-container--paused" : "")
            }
          >
            <video
              id="pres-video"
              className="pres-video"
              ref={ref => (this.videoRef = ref)}
              playsInline
              loop
              muted
            >
              <source src={introVideo} />
            </video>
            <div
              onClick={() => this.onPlayClick()}
              className="control-backdrop"
            >
              <img
                className="play-btn"
                alt="video-control"
                src={videoControl}
              />
            </div>
          </div>
        </div>
      </div>
    );
  }
}
