import React, { Component } from 'react';
import out from "../img/Out.svg";
import { connect } from 'react-redux';
import { createSelector } from 'reselect';
import "../styles/Concept.css";

class Concept extends Component {
  componentDidMount() {
    this.counter = 0;
    this.updateRate = 10;
    this.mouse = {
      _x: 0,
      _y: 0,
      x: 0,
      y: 0,
      updatePosition: function(event) {
        var e = event || window.event;
        this.x = e.clientX - this._x;
        this.y = (e.clientY - this._y) * -1;
      },
      setOrigin: function (e) {
        this._x = e.getBoundingClientRect().left + Math.floor(e.getBoundingClientRect().width / 2);
        this._y = e.getBoundingClientRect().top + Math.floor(e.getBoundingClientRect().height / 2);
      },
    };
    this.mouse.setOrigin(this.readOnMediumContainer);
  }

  componentDidUpdate(prevProps) {
    const { selected_concept } = this.props;
    if (prevProps.selected_concept !== selected_concept) {
      setTimeout(() => {
        this.mouse.setOrigin(this.readOnMediumContainer);
      }, 400)
    }
  }

  update(e) {
    this.mouse.updatePosition(e);
    this.updateTransformStyle(
      (this.mouse.y / this.readOnMedium.offsetHeight / 2).toFixed(2),
      (this.mouse.x / this.readOnMedium.offsetWidth / 2).toFixed(2)
    );
  }

  isTimeToUpdate () {
    return this.counter++ % this.updateRate === 0;
  };

  updateTransformStyle(x,y) {
    let style = "rotateX(" + x + "deg) rotateY(" + y + "deg)";
    this.readOnMedium.style.transform = style;
  }

  onMouseMove(e) {
    if (this.isTimeToUpdate()) {
      this.update(e);
    }
  }

  onMouseEnter(e) {
    this.update(e);
  }

  onMouseLeave() {
    this.readOnMedium.style = "";
  }

  render() {
    const { img_url, title, description, id, selected_concept, credits } = this.props;

    return (
      <div id={id} className="concept-container" style={{backgroundImage: "url(" + img_url + ")"}}>
        <div className={"concept-intro-container " + (selected_concept === id ? "selected-concept" : "")}>
          {credits && <p className={"credits " + (selected_concept === id ? "selected-credits" : "")}>
            {credits}
          </p>}
          <h2>
            {title}
          </h2>
          <p>
            {description}
          </p>
          <div 
            ref={(ref) => this.readOnMediumContainer = ref}
            onMouseMove={(e) => this.onMouseMove(e)}
            onMouseEnter={(e) => this.onMouseEnter(e)}
            onMouseLeave={() => this.onMouseLeave()} 
            className="read-on-medium-container">
            <div ref={(ref) => this.readOnMedium = ref} className="read-on-medium">
              <p>
                Read on Medium
              </p>
              <img src={out} alt="out" />
            </div>
          </div>
        </div>
      </div>
    )
  }
}

const selector = createSelector(
  state => state['selected_concept'],
  (
    selected_concept
) => {
    return  {
      selected_concept
    };
  }
);

export default connect(selector)(Concept);
