import React, { Component } from 'react';
import { connect } from 'react-redux';
import { createSelector } from 'reselect';
import { bindActionCreators } from 'redux';
import {
  openPanel,
  selectConcept
} from '../actions/index.js';

class Concept extends Component {
  render() {
    const { title, date, description, id, concepts } = this.props;
    const concepts_keys = Object.keys(concepts);
    const last_id = concepts_keys[concepts_keys.length - 1];

    return (
      <div
        style={id === last_id ? { borderBottom:"none" } : {}}
        className="concept">
        <p className="hat">
          {date.toUpperCase()}
        </p>
        <h2 className="head">
          {title}
        </h2>
        <p className="description">
          {description}
        </p>
        <div className="CTA-row">
          <div
            onClick={() => {
              this.props.selectConcept(id);
              this.props.openPanel(true, "concepts");
            }}
            className="CTA">
            READ STORY
          </div>
          <div className="sub-CTA">

          </div>
          <div className="sub-CTA">

          </div>
        </div>
      </div>
    );
  }
}

function matchDispatchToProps(dispatch) {
  return bindActionCreators({
    openPanel,
    selectConcept
  }, dispatch)
}

const selector = createSelector(
  state => state['concepts'],
  (
    concepts
) => {
    return  {
      concepts
    };
  }
);

export default connect(selector, matchDispatchToProps)(Concept);
