import React, { Component } from 'react';
import { connect } from 'react-redux';
import { createSelector } from 'reselect';
import { bindActionCreators } from 'redux';
import {
  openPanel
} from '../actions/index.js';
import close from '../img/Close.png';

class Panel extends Component {
  panelStyle() {
    const { window_dimensions, panel } = this.props;

    if (!panel.isOpen) {
      return {
        transform: `translateY(-(${window_dimensions.height+20}))`
      }
    }
  }

  render() {
    return (
      <div
        style={this.panelStyle()}
        className="panel">
        <div className="panel-header">
          <h1 className="big-title">
            iOS Mogi
          </h1>
          <img
            onClick={() => this.props.openPanel(false, "")}
            src={close}
            alt="Close"
            className="close"
          />
        </div>
      </div>
    );
  }
}

function matchDispatchToProps(dispatch) {
  return bindActionCreators({
    openPanel
  }, dispatch)
}

const selector = createSelector(
  state => state['window_dimensions'],
  state => state['panel'],
  (
    window_dimensions,
    panel
) => {
    return  {
      window_dimensions,
      panel
    };
  }
);

export default connect(selector, matchDispatchToProps)(Panel);
