import React, { Component } from "react";
import $ from "jquery";
import "../styles/Hero.css";
import mogi from "../img/Mogi.jpg";
import chatroom from "../img/FujiSan.jpg";
import mac from "../img/MacOS.jpeg";
import youtube from "../img/Youtube.jpg";
import ipadOS from "../img/iPad.jpg";

export default class Hero extends Component {
  render() {
    const { hasScrolled } = this.props;

    return (
      <div id="hero" className="section hero">
        <div className="max-width">
          <div className="hero-tags-container">
            <h2 className="hero-tag">
              Hi there! <br /> I'm a <span>Designer</span>, Engineer and
              Storywriter from France!
            </h2>
          </div>
          <p className="hero-description">
            Crafting delight at Avanade.
            <br /> Spending most of my free time writing and designing.
          </p>
          <button
            onClick={() => {
              $([document.documentElement, document.body]).animate(
                {
                  scrollTop: $("#articles").offset().top
                },
                800
              );
            }}
            className="button"
          >
            Get Started
          </button>
        </div>

        <div
          onClick={() => {
            $([document.documentElement, document.body]).animate(
              {
                scrollTop: $("#articles").offset().top
              },
              800
            );
          }}
          className={
            "scroll-container " +
            (hasScrolled ? "scroll-container--transparent" : "")
          }
        >
          <p className="scroll-text">Scroll for more love</p>
          <div className="scroll-bar" />
        </div>
      </div>
    );
  }
}
