import React, { Component } from "react";
import { connect } from "react-redux";
import { createSelector } from "reselect";
import { bindActionCreators } from "redux";
import {
  switchToMobile,
  switchToDesktop,
  storeWindowDimensions,
  selectConcept
} from "../actions/index.js";
import Header from "./Header.js";
import "../styles/App.css";
import "../styles/Section.css";
import map from "lodash/map";
import Concept from "./Concept.js";
import Hero from "./Hero.js";
import Articles from "./Articles.js";

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      hasScrolled: false,
      antiHeader: false
    };
  }

  updateDimensions() {
    const { window_dimensions } = this.props;
    this.timeout && clearTimeout(this.timeout);

    this.timeout = setTimeout(() => {
      this.props.storeWindowDimensions(window.innerWidth, window.innerHeight);
    }, 400);

    if (window.innerWidth < 810) {
      // Trigger mobile version
      !window_dimensions.isMobile && this.props.switchToMobile();
    } else {
      // Trigger desktop version
      !window_dimensions.isDesktop && this.props.switchToDesktop();
    }
  }

  handleScroll() {
    if (window.scrollY > 80) {
      this.setState({ hasScrolled: true });
    } else {
      this.setState({ hasScrolled: false });
    }

    if (
      window.scrollY > window.innerHeight &&
      window.scrollY < 2 * window.innerHeight
    ) {
      this.setState({ antiHeader: true });
    } else if (window.scrollY < window.innerHeight) {
      this.setState({ antiHeader: false });
    }
  }

  componentDidMount() {
    // const isSafari = /^((?!chrome|android).)*safari/i.test(navigator.userAgent);
    window.addEventListener("resize", this.updateDimensions.bind(this));
    window.addEventListener("scroll", this.handleScroll.bind(this));
    let vh = window.innerHeight * 0.01;
    document.documentElement.style.setProperty("--vh", `${vh}px`);
    const { window_dimensions } = this.props;
    this.props.storeWindowDimensions(window.innerWidth, window.innerHeight);
    if (window.innerWidth < 810) {
      // Trigger mobile version
      !window_dimensions.isMobile && this.props.switchToMobile();
    } else {
      // Trigger desktop version
      !window_dimensions.isDesktop && this.props.switchToDesktop();
    }
  }

  render() {
    const isSafari = /^((?!chrome|android).)*safari/i.test(navigator.userAgent);

    return (
      <div className={"main-body " + (!isSafari ? "main-body-not-safari" : "")}>
        <Header hasScrolled antiHeader />
        <Hero hasScrolled />
        <Articles />
        {/* {this.renderConcepts()}
        <Presentation canScroll={canScroll} /> */}
      </div>
    );
  }
}

function matchDispatchToProps(dispatch) {
  return bindActionCreators(
    {
      storeWindowDimensions,
      switchToMobile,
      switchToDesktop,
      selectConcept
    },
    dispatch
  );
}

const selector = createSelector(
  state => state["window_dimensions"],
  state => state["concepts"],
  state => state["selected_concept"],
  (window_dimensions, concepts, selected_concept) => {
    return {
      window_dimensions,
      concepts,
      selected_concept
    };
  }
);

export default connect(
  selector,
  matchDispatchToProps
)(App);
