import React, { Component } from "react";

export default class ArticleImage extends Component {
  componentWillReceiveProps(nextProps) {
    const { index } = this.props;
    if (nextProps.currentMediaIndex === index) {
      this.videoRef.play();
      this.setState({ videoPlayed: true });
    } else {
      this.videoRef.pause();
      this.setState({ videoPlayed: false });
    }
  }

  render() {
    const { imgSrc, mediaDescription, videoPlayed } = this.props;
    return (
      <div
        onClick={() => this.onPlayClick()}
        className={
          "article-media-container " +
          (!videoPlayed ? "article-media-container--paused" : "")
        }
      >
        <img
          alt="Media"
          onLoad={() => console.log("hey")}
          src={imgSrc}
          className="article-media"
        />
        <p
          className={
            "article-media-description " +
            (videoPlayed ? "article-media-description--played" : "")
          }
        >
          {mediaDescription}
        </p>
      </div>
    );
  }
}
