  {
    content: "The past three years have been extremely enriching.",
    show: false,
    disappear: false
  },
  {
    content: "The past three years have been extremely enriching.",
    show: false,
    disappear: false
  },
  {
    content: "The past three years have been extremely enriching.",
    show: false,
    disappear: false
  }
];

export default (state = initialState, action) => {
  switch (action.type) {
    case "SHOW_INTRO":
      return initialState.map((intro, index) => {
        if (index !== action.index) {
          return item;
        }

        return {
          ...item,
          show: action.payload
        };
      });
  }
  return state;
};
