import React, { Component } from 'react';
import { connect } from 'react-redux';
import { createSelector } from 'reselect';
import { bindActionCreators } from 'redux';
import '../styles/Mobile.css';
import logo from '../img/Logo.png';
import { openBurger } from '../actions/index.js';
import Tab from './Tab.js';
import map from 'lodash/map';

class MobileHead extends Component {
  renderTab() {
    const { tabs } = this.props;

    return(
      map(
        tabs,
        (tab, id) => <Tab key={id} {...tab} id={id} />
      )
    )
  }

  render() {
    const { border, panel, burger } = this.props;

    return (
      <div
        style={(border || panel.isOpen) ? {borderBottom: "solid 1px rgba(0,0,0,0.1)"} : {}}
        className="mobile-head">
        <div id="top-of-the-world" className="header">
          <img src={logo} alt="Logo" className="logo" />
          <p>
            KEVIN EUGENE
          </p>
        </div>
        <div onClick={() => {
          !burger && this.props.openBurger(true);
          burger && this.props.openBurger(false);
        }}
        className="hamburger">
          <div className="top-burger">

          </div>
          <div style={burger ? {overflow: "visible", height: 100} : {height: 0}} className="mobile-chapters-container">
            {this.renderTab()}
          </div>
          <div className="bottom-burger">

          </div>
        </div>
      </div>
    );
  }
}

function matchDispatchToProps(dispatch) {
  return bindActionCreators({
    openBurger
  }, dispatch)
}

const selector = createSelector(
  state => state['panel'],
  state => state['burger'],
  state => state['tabs'],
  (
    panel,
    burger,
    tabs
) => {
    return  {
      panel,
      burger,
      tabs
    };
  }
);

export default connect(selector, matchDispatchToProps)(MobileHead);
