import React, { Component } from "react";
import { connect } from "react-redux";
import { createSelector } from "reselect";

class NextArticle extends Component {
  render() {
    const { selected_concept, concepts } = this.props;
    const prompt =
      selected_concept === "mogi" ? "Next Article" : "Previous Article";
    const name = selected_concept === "mogi" ? "Youtube 2.0" : "iOS Mogi";

    return (
      <div className="next-article-container">
        <div className="next-article-image-container">
          <div className="next-article-image-gradient" />
          <div className="next-article-image" />
        </div>
        <p className="next-article-prompt">{prompt}</p>
        <h1 className="next-article-name">{name}</h1>
      </div>
    );
  }
}

const selector = createSelector(
  state => state["selected_concept"],
  state => state["concepts"],
  (selected_concept, concepts) => {
    return {
      selected_concept,
      concepts
    };
  }
);

export default connect(selector)(NextArticle);
