import React, { Component } from 'react';
import { connect } from 'react-redux';
import { createSelector } from 'reselect';
import '../styles/Mobile.css';
import logo from '../img/Logo.png';
import { openBurger } from '../actions/index.js';

class MobileHead extends Component {
  render() {
    const { border, panel, burger } = this.props;

    return (
      <div
        style={(border || panel.isOpen) ? {borderBottom: "solid 1px rgba(0,0,0,0.1)"} : {}}
        className="mobile-head">
        <div id="top-of-the-world" className="header">
          <img src={logo} alt="Logo" className="logo" />
          <p>
            KEVIN EUGENE
          </p>
        </div>
        <div onClick={() => this.props.openBurger(true)} className="hamburger">
          <div className="top-burger">

          </div>
          <div style={burger ? {} : {height: 0}} className="mobile-chapters-container">

          </div>
          <div className="bottom-burger">

          </div>
        </div>
      </div>
    );
  }
}

function matchDispatchToProps(dispatch) {
  return bindActionCreators({
    openBurger
  }, dispatch)
}

const selector = createSelector(
  state => state['panel'],
  state => state['burger'],
  (
    panel,
    burger
) => {
    return  {
      panel,
      burger
    };
  }
);

export default connect(selector, matchDispatchToProps)(MobileHead);
