import React, { Component } from 'react'
import { connect } from 'react-redux';
import { createSelector } from 'reselect';
import { bindActionCreators } from 'redux';
import {
  switchToMobile,
  switchToDesktop,
  storeWindowDimensions,
} from '../actions/index.js';
import Header from './Header.js';
import "../styles/App.css";
import mogi_url from '../img/Mogi.jpeg';
import macos_url from '../img/MacOS.jpeg';
import fuji_url from '../img/FujiSan.jpeg';


class App extends Component {
  updateDimensions() {
    const { window_dimensions } = this.props;
    this.timeout && clearTimeout(this.timeout);
    this.timeout = setTimeout(() => {
      this.props.storeWindowDimensions(window.innerWidth, window.innerHeight);
    }, 400);

    if (window.innerWidth < 810) {
      // Trigger mobile version
      !window_dimensions.isMobile && this.props.switchToMobile();
    }
    else {
      // Trigger desktop version
      !window_dimensions.isDesktop && this.props.switchToDesktop();
    }
  }

  componentDidMount() {
    window.addEventListener("resize", this.updateDimensions.bind(this));
    const { window_dimensions } = this.props;
    this.props.storeWindowDimensions(window.innerWidth, window.innerHeight);
    if (window.innerWidth < 810) {
      // Trigger mobile version
      !window_dimensions.isMobile && this.props.switchToMobile();
    }
    else {
      // Trigger desktop version
      !window_dimensions.isDesktop && this.props.switchToDesktop();
    }
  }

  render() {
    return (
      <div className="main-body">
        <Header />
        <div className="concept-container" style={{backgroundImage: "url(" + macos_url + ")"}}>
        </div>
        <div className="concept-container" style={{backgroundImage: "url(" + mogi_url + ")"}}>
        </div>
        <div className="concept-container" style={{backgroundImage: "url(" + fuji_url + ")"}}>
        </div>
        <div className="concept-container" style={{backgroundImage: "url(" + mogi_url + ")"}}>
        </div>
      </div>
    )
  }
}

function matchDispatchToProps(dispatch) {
  return bindActionCreators({
    storeWindowDimensions,
    switchToMobile,
    switchToDesktop,
  }, dispatch)
}

const selector = createSelector(
  state => state['window_dimensions'],
  (
    window_dimensions,
) => {
    return  {
      window_dimensions,
    };
  }
);

export default connect(selector, matchDispatchToProps)(App);
