import React, { Component } from "react";

export default class Intro extends Component {
  render() {
    const { content, show, disappear } = this.props;
    return (
      <p
        className={
          "intro " +
          (show ? "shown-intro " : "") +
          (disappear ? "disappeared-intro" : "")
        }
      >
        {content}
      </p>
    );
  }
}
