import React, { Component } from 'react';
import { connect } from 'react-redux';
import { createSelector } from 'reselect';
import { bindActionCreators } from 'redux';
import Concepts from './Concepts.js';
import About from './About.js';
import Side from './Side.js';
import Panel from './Panel.js';
import '../styles/App.css';
import '../styles/Panel.css';
import {
  storeWindowDimensions,
  switchToMobile,
  switchToDesktop
} from '../actions/index.js';
import logo from '../img/Logo.png';

class App extends Component {
  updateDimensions() {
    const { window_dimensions } = this.props;
    this.timeout && clearTimeout(this.timeout);
    this.timeout = setTimeout(() => {
      this.props.storeWindowDimensions(window.innerWidth, window.innerHeight);
    }, 400);

    if (window.innerWidth < 650) {
      // Trigger mobile version
      !window_dimensions.isMobile && this.props.switchToMobile();
    }
    else {
      // Trigger desktop version
      !window_dimensions.isDesktop && this.props.switchToDesktop();
    }
  }

  componentDidMount() {
    window.addEventListener("resize", this.updateDimensions.bind(this));
    const { window_dimensions } = this.props;
    this.props.storeWindowDimensions(window.innerWidth, window.innerHeight);
    if (window.innerWidth < 650) {
      // Trigger mobile version
      !window_dimensions.isMobile && this.props.switchToMobile();
    }
    else {
      // Trigger desktop version
      !window_dimensions.isDesktop && this.props.switchToDesktop();
    }

    this.handleScroll = this.handleScroll.bind(this);
    window.addEventListener("scroll", this.handleScroll);
  }

  componentWillUnmount() {
    window.removeEventListener("scroll", this.handleScroll);
    this.handleScroll = undefined;
  }

  handleScroll() {
    const { window_dimensions } = this.props;
    const page_center = window_dimensions.height/2 + 10;
    const intros_array = document.getElementsByClassName("intro");
    const intros_center = [];
    for (let i = 0; i < intros_array.length; i++) {
      intros_center[i] = (intros_array[i].getBoundingClientRect().top + intros_array[i].getBoundingClientRect().bottom)/2
    }
    const distance_to_center = [];
    for (let i = 0; i < intros_center.length; i++) {
      distance_to_center[i] = Math.abs(intros_center[i] - page_center);
    }
    const minimum_distance = Math.min(...distance_to_center);
    const index_of_min = distance_to_center.indexOf(minimum_distance);
    const id_to_focus = Object.keys(intros)[index_of_min];
    id_to_focus !== intro_focus && this.props.introFocus(id_to_focus);
  }

  render() {
    const {Â panel } = this.props;

    return (
      <div className="App">
        <Side />
        <div className="content">
          <div className="header">
            <img src={logo} alt="Logo" className="logo" />
            <p>
              KEVIN EUGENE
            </p>
          </div>
          <h1 className="big-title">
            Welcome to my portfolio
          </h1>
          <p className="intro">
            This is my portfolio. Please scroll down to have a look at a few of my UI-UX projects.
          </p>
          <Concepts />
          <About />
          {/* {panel.isOpen && <Panel />} */}
          <Panel />
        </div>
      </div>
    );
  }
}

function matchDispatchToProps(dispatch) {
  return bindActionCreators({
    storeWindowDimensions,
    switchToMobile,
    switchToDesktop
  }, dispatch)
}


const selector = createSelector(
  state => state['window_dimensions'],
  state => state['panel'],
  (
    window_dimensions,
    panel
) => {
    return  {
      window_dimensions,
      panel
    };
  }
);

export default connect(selector, matchDispatchToProps)(App);
