import React, { Component } from "react";
import { connect } from "react-redux";
import { createSelector } from "reselect";
import { bindActionCreators } from "redux";
import introVideo from "../videos/Portfolio.mp4";
import "../styles/Presentation.css";
import pause from "../img/Pause.svg";
import play from "../img/Play.svg";
import map from "lodash/map";
import Intro from "./Intro";
import { showIntro, disappearIntro } from "../actions/index.js";

class Presentation extends Component {
  constructor(props) {
    super(props);

    this.state = {
      paused: true,
      showPresentation: false
    };
  }

  onPlayClick() {
    const { paused } = this.state;
    this.setState({ paused: !paused });
    paused && this.videoRef.play();
    !paused && this.videoRef.pause();
  }

  render() {
    const { paused } = this.state;
    const videoControl = paused ? play : pause;
    return (
      <div className="section">
        <div className="max-width">
          <p className="section-sub">
            Images are worth more than a thousand words
          </p>
          <h3 className="section-title">About Me</h3>
          <div
            className={
              "video-container " + (paused ? "video-container--paused" : "")
            }
          >
            <video
              className="pres-video"
              ref={ref => (this.videoRef = ref)}
              playsInline
              loop
              muted
            >
              <source src={introVideo} />
            </video>
            <div className="control-backdrop">
              <div onClick={() => this.onPlayClick()} className="play-btn">
                <img
                  alt="video-control"
                  src={videoControl}
                  style={paused ? { marginLeft: 2 } : {}}
                />
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

function matchDispatchToProps(dispatch) {
  return bindActionCreators(
    {
      showIntro,
      disappearIntro
    },
    dispatch
  );
}

const selector = createSelector(
  state => state["intro"],
  intro => {
    return {
      intro
    };
  }
);

export default connect(
  selector,
  matchDispatchToProps
)(Presentation);
