import React, { Component } from "react";
import pause from "../img/Pause.svg";
import play from "../img/Play.svg";
import "../styles/ArticleVideo.css";

export default class ArticleVideo extends Component {
  constructor(props) {
    super(props);

    this.state = {
      videoPlayed: false
    };
  }

  onPlayClick() {
    const { videoPlayed } = this.state;
    this.setState({ videoPlayed: !videoPlayed });
    if (videoPlayed) {
      this.videoRef.pause();
    } else {
      this.videoRef.play();
    }
  }

  componentWillReceiveProps(nextProps) {
    const { index } = this.props;
    if (nextProps.currentVideoIndex === index) {
      this.videoRef.play();
    } else {
      this.videoRef.pause();
    }
  }

  render() {
    const { videoSrc, videoDescription } = this.props;
    const { videoPlayed } = this.state;
    const videoControl = !videoPlayed ? play : pause;
    return (
      <div
        onClick={() => this.onPlayClick()}
        className={
          "article-video-container " +
          (!videoPlayed ? "article-video-container--paused" : "")
        }
      >
        <video
          playsInline
          loop
          muted
          ref={ref => (this.videoRef = ref)}
          src={videoSrc}
          className="article-video"
        />
        <div onClick={() => this.onPlayClick()} className="control-backdrop">
          <img className="play-btn" alt="video-control" src={videoControl} />
        </div>
        <p
          className={
            "article-video-description " +
            (videoPlayed ? "article-video-description--played" : "")
          }
        >
          {videoDescription}
        </p>
      </div>
    );
  }
}
