import React, { Component } from 'react';
import { connect } from 'react-redux';
import { createSelector } from 'reselect';
import { bindActionCreators } from 'redux';
import {
  openPanel,
  selectTab
} from '../actions/index.js';

class Tab extends Component {
  tabStyle() {
    const { panel, id, selected_tab, tabs, window_dimensions, burger } = this.props;

    if (window_dimensions.isMobile) {
      const tabs_ids = Object.keys(tabs);
      const tab_index = tabs_ids.indexOf(id);
      if (burger) {
        if (id === selected_tab) {
          return {
            backgroundColor: "white",
            color: "black",
            transitionDuration: "0.5s",
            opacity: 1,
            transitionDelay: "0.2s"
          }
        }
        else {
          return {
            opacity: 1,
            transitionDelay: "0.2s"
          }
        }
      }
    }
    else {
      if (id === selected_tab) {
        return {
          backgroundColor: "white",
          color: "black",
          transitionDuration: "0.5s"
        }
      }
      else {
        if (panel.isOpen) {
          if (panel.flavor !== id) {
            return {
              opacity: 0.3,
              transitionDuration: "0.5s"
            }
          }
          else {
            return {
              backgroundColor: "white",
              color: "black",
              transitionDuration: "0.5s"
            }
          }
        }
      }
    }
  }

  render() {
    const { text, panel, id, window_dimensions } = this.props;
    return (
      <li
        onClick={() => {
          const section_DOM = document.getElementById(id);
          section_DOM.childNodes[0].scrollIntoView({
            behavior: 'smooth'
          });
          if (panel.isOpen) {
            this.props.openPanel(false, "");
          }
        }}
        style={this.tabStyle()}>
        {text.toUpperCase()}
      </li>
    );
  }
}

function matchDispatchToProps(dispatch) {
  return bindActionCreators({
    openPanel,
    selectTab
  }, dispatch)
}

const selector = createSelector(
  state => state['panel'],
  state => state['selected_tab'],
  state => state['window_dimensions'],
  state => state['tabs'],
  state => state['burger'],
  (
    panel,
    selected_tab,
    window_dimensions,
    tabs,
    burger
) => {
    return  {
      panel,
      selected_tab,
      window_dimensions,
      tabs,
      burger
    };
  }
);

export default connect(selector, matchDispatchToProps)(Tab);
