import React, { Component } from 'react';
import { connect } from 'react-redux';
import { createSelector } from 'reselect';
import { bindActionCreators } from 'redux';
import {
  openPanel
} from '../actions/index.js';
import close from '../img/Close.png';
import map from 'lodash/map';
import Chapter from './Chapter.js';
import Mogi from './Mogi.js';

class Panel extends Component {
  constructor(props) {
    super(props);

    this.state = {
      desync_panel_open: false,
      render_content: false,
      desync_content_ready: false
    }
  }

  componentDidMount() {
    setTimeout(() => {
      this.setState({
        desync_panel_open: true
      })
    }, 10);
    setTimeout(() => {
      this.setState({
        render_content: true
      })
    }, 850);
    setTimeout(() => {
      this.setState({
        desync_content_ready: true
      })
    }, 9000);
  }

  componentDidUpdate(prevProps) {
    const { panel } = this.props;

    if (prevProps.panel.isOpen && !panel.isOpen) {
      this.setState({
        desync_panel_open: false
      })
    }
  }

  panelStyle() {
    const { window_dimensions } = this.props;
    const { desync_panel_open } = this.state;

    if (!desync_panel_open) {
      return {
        transform: `translateY(-${window_dimensions.height+30}px)`
      }
    }
  }

  panelContentStyle() {
    const { desync_content_ready } = this.state;

    if (!desync_content_ready) {
      return {
        transform: "translateY(100px)",
        opacity: 0
      }
    }
  }

  renderChapters() {
    const { chapters } = this.props;

    return(
      map(
        chapters.mogi,
        (chapter, id) => <Chapter key={id} {...chapter} id={id} />
      )
    );
  }

  render() {
    const { render_content } = this.state;

    return (
      <div
        style={this.panelStyle()}
        className="panel">
        <div className="panel-top">
          <div className="panel-header">
            <h1 className="big-title">
              iOS Mogi
            </h1>
            <img
              onClick={() => this.props.openPanel(false, "")}
              src={close}
              alt="Close"
              className="close"
            />
          </div>
          <div className="concept-chapters">
            {this.renderChapters()}
          </div>
        </div>
        <div
          className="panel-container"
          style={this.panelContentStyle()}>
          {render_content && <Mogi />}
        </div>
      </div>
    );
  }
}

function matchDispatchToProps(dispatch) {
  return bindActionCreators({
    openPanel
  }, dispatch)
}

const selector = createSelector(
  state => state['window_dimensions'],
  state => state['panel'],
  state => state['chapters'],
  (
    window_dimensions,
    panel,
    chapters
) => {
    return  {
      window_dimensions,
      panel,
      chapters
    };
  }
);

export default connect(selector, matchDispatchToProps)(Panel);
